package com.syntax.reviewclass04;

public class forLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int x=0;x<5; x++) {
			System.out.println("batch 12 is best");
		}
		
		
		
		
		int i=0;
		for(System.out.println("1");i<5;System.out.println("2")) {
			System.out.println("batch12");i++;
		}
		
		
		
		
		
	}

}
